from PIL import Image
from random import randint
from itertools import repeat
from io import BytesIO

def favicon():
  img_io = BytesIO()
  img = Image.new("RGB", (32,32))
  colors = [random_color() for i in range(4)]
  line1 = (colors[0]) * 16 + (colors[1]) * 16
  line2 = (colors[2]) * 16 + (colors[3]) * 16
  favicon = tuple(line1 * 16 + line2 * 16)
  img.putdata(favicon)
  img.save(img_io, 'PNG', quality=70)
  img_io.seek(0)
  return img_io

def random_color(n = 1):
  return tuple((randint(0,255), randint(0,255), randint(0,255)) for i in range(n))
